package xuyuji.leetcode.Q0008_StringtoIntegerAtoi;

import static org.junit.Assert.assertEquals;

import org.junit.Before;
import org.junit.Test;

public class Solution1Test {

	private Solution1 solution;

	@Before
	public void setup() {
		solution = new Solution1();
	}

	@Test
	public void testMyAtoi1() {
		assertEquals(0, solution.myAtoi(" "));
	}

	@Test
	public void testMyAtoi2() {
		assertEquals(42, solution.myAtoi("42"));
	}

	@Test
	public void testMyAtoi3() {
		assertEquals(-42, solution.myAtoi("   -42"));
	}

	@Test
	public void testMyAtoi4() {
		assertEquals(4193, solution.myAtoi("4193 with words"));
	}

	@Test
	public void testMyAtoi5() {
		assertEquals(0, solution.myAtoi("words and 987"));
	}

	@Test
	public void testMyAtoi6() {
		assertEquals(-2147483648, solution.myAtoi("-91283472332"));
	}

	@Test
	public void testMyAtoi7() {
		assertEquals(2147483647, solution.myAtoi(
				""));
	}

	@Test
	public void testMyAtoi8() {
		assertEquals(0, solution.myAtoi(null));
	}

	@Test
	public void testMyAtoi9() {
		assertEquals(0, solution.myAtoi("+"));
	}

	@Test
	public void testMyAtoi10() {
		assertEquals(0, solution.myAtoi("-"));
	}
}
